import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import { MDBCol, MDBRow } from "mdb-react-ui-kit";


//Components
import Date from "../../Components/date";

//Images
import BackArrow from "../../Assets/Back_Button.png"

const Campaigns = () => {
  const { seoName, id } = useParams();
  const [promotion, setPromotion] = useState(null);

  useEffect(() => {
    fetchPromotion();
  }, []);

  const fetchPromotion = async () => {
    try {
      const response = await axios.get(
        `https://api.extrazone.com/promotions?Id=${id}`,
        {
          headers: {
            "X-Country-Id": "TR",
            "X-Language-Id": "TR",
          },
        }
      );
      setPromotion(response.data);
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  if (!promotion) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <MDBRow>
          <MDBCol>
            <img src={promotion.ImageUrl} alt="details-img" /> 
            <img
                  src={promotion.BrandIconUrl}
                  alt="details-brand"
            />
              <img className="details-backArrow" src={BackArrow}/>

            <Date />

          </MDBCol>
      </MDBRow>
    </div>
  );
};

export default Campaigns;
